import { Component,OnInit } from '@angular/core';
import { User } from '../../../Models/User';
import { Inject } from '@angular/core';
import { UserService } from '../../../Services/user.service';

@Component({
  selector: 'app-user-detail',
  templateUrl: './user-detail.component.html',
})
export class UserDetailComponent implements OnInit {
  selectedUser: User;
 constructor(
    @Inject(USER_TOKEN) private userService: UserService
  ) {}

  ngOnInit(){
    this.userService.OnUserDetailsClicked.subscribe((data:User)=>{
      this.selectedUser = data;
      console.log(this.selectedUser);
    });
  }

  // constructor(private userservice:UserService) {
  //  }
  //  userlist = this.userservice.getAllUsers();

}
<div class="user-detail-container">
    <div>
        <img class="user-detail-avatar-image" src="./assets/images/male-avatar.png">
    </div>
    <div class="user-detail-info" >
        <h2>User Detail</h2>
        <p><b>Name:</b> John Smith</p>
        <p><b>Gender:</b> Male</p>
        <p><b>Subscription Type:</b> Yearly</p>
        <p><b>Status:</b> Active</p>
    </div>    
</div>
