import { Component, Inject } from '@angular/core';
import { SubscribeService } from '../../Services/subscribe.service';
import { UserService } from '../../Services/user.service';
import { FormsModule } from '@angular/forms';
import { CommonModule } from '@angular/common';

@Component({
  standalone: true,
  selector: 'app-admin',
  templateUrl: './admin.component.html',
  imports: [CommonModule,FormsModule],
  providers: [SubscribeService,UserService]

})
export class AdminComponent {
  constructor(private userService: UserService) { 

  }

  name: string = '';
  gender: string = 'Male';
  subscriptionType: string = 'Yearly';
  status: string = 'Active';

  createUser(){
    this.userService.createUser(this.name, this.gender, this.subscriptionType, this.status);
    console.log('User Created');
  }


  
}
<div class="admin-container">
    <div class="admin-add-user">
        <input type="text" placeholder="User Name" [(ngModel)]="name">
        <select name="gender" [(ngModel)]="gender">
            <option value="Male">Male</option>
            <option value="Female">Female</option>
        </select>
        <br>
        <select name="subscriptionType" [(ngModel)]="subscriptionType">
            <option value="Monthly">Monthly</option>
            <option value="Quaterly">Quaterly</option>
            <option value="Yearly">Yearly</option>
        </select>
        <select name="active" [(ngModel)]="status">
            <option value="Active">Active</option>
            <option value="Inactive">Inactive</option>
        </select>
        <br>
        <button class="admin-button" (click)="createUser()">CREAT</button>
    </div>
    <app-user-list></app-user-list>
    <app-user-detail></app-user-detail>
</div>
